int pack_rgb(unsigned char r, unsigned char g, unsigned char b)
{
	return (r << 16) | (g << 8) | b;
}

void unpack_rgb(int rgb, unsigned char *r, unsigned char *g, unsigned char *b)
{
	*r = (rgb >> 16) & 0xFF;
	*g = (rgb >> 8) & 0xFF;
	*b = rgb & 0xFF;
}

colormap_t cmap_parse(char *s)
{
	int num_colormaps = sizeof(COLORMAP_CONFIGS) / sizeof(colormap_config_t);
	for (int i = 0; i < num_colormaps; i++)
	{
		colormap_config_t cc = COLORMAP_CONFIGS[i];
		if (strcmp(s, cc.name) == 0)
			return cc.id;
	}

	return COLORMAP_GRAY;
}

int _cmap_find(colormap_t cmap)
{
	int num_colormaps = sizeof(COLORMAP_CONFIGS) / sizeof(colormap_config_t);
	for (int i = 0; i < num_colormaps; i++)
		if (COLORMAP_CONFIGS[i].id == cmap)
			return i;

	return -1;
}

int cmap_get(colormap_t cmap, double v)
{
	if (v < 0.0)
		v = 0.0;
	else if (v > 1.0)
		v = 1.0;
	else if (c_isnan(v))
		return 0x000000;

	int cmap_idx = _cmap_find(cmap);
	colormap_config_t cmap_cfg = COLORMAP_CONFIGS[cmap_idx];
	
	double res_min_1 = (double) CMAP_RESOLUTION - 1.0;
	int i1 = (int) c_floor(v * res_min_1);
	int i2 = (int) c_ceil(v * res_min_1);
	
	unsigned char c1_red = cmap_cfg.data[i1 * 3];
	unsigned char c1_green = cmap_cfg.data[i1 * 3 + 1];
	unsigned char c1_blue = cmap_cfg.data[i1 * 3 + 2];

	if (i1 == i2)
		return pack_rgb(c1_red, c1_green, c1_blue);

	unsigned char c2_red = cmap_cfg.data[i2 * 3];
	unsigned char c2_green = cmap_cfg.data[i2 * 3 + 1];
	unsigned char c2_blue = cmap_cfg.data[i2 * 3 + 2];

	double v1 = (double) i1 / res_min_1;
	double v2 = (double) i2 / res_min_1;
	double t = (v - v1) / (v2 - v1);
	unsigned char c_red = (unsigned char) c_floor(c1_red + t * (c2_red - c1_red));
	unsigned char c_green = (unsigned char) c_floor(c1_green + t * (c2_green - c1_green));
	unsigned char c_blue = (unsigned char) c_floor(c1_blue + t * (c2_blue - c1_blue));

	return pack_rgb(c_red, c_green, c_blue);
}